  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'login',
	title: 'Login'
};
export const metadata = {
  "id": "support_functions/login",
  "title": "Login",
  "description": "This functionality supports users logging in for the first time as well as the ongoing secure identification and provision of access to users.",
  "source": "@site/docs/support_functions/Login_8db5ac07d5ed48599beebedfb58dd273.mdx",
  "permalink": "/opencrvs-core/docs/support_functions/login",
  "sidebar": "docs",
  "previous": {
    "title": "Record verification",
    "permalink": "/opencrvs-core/docs/core_functions/recordVerification"
  },
  "next": {
    "title": "Performance management",
    "permalink": "/opencrvs-core/docs/support_functions/performanceManagement"
  }
};
  /* @jsx mdx */
import useBaseUrl from "@docusaurus/useBaseUrl";
export const rightToc = [{
  value: 'Configuration',
  id: 'configuration',
  children: []
}, {
  value: 'User Stories',
  id: 'user-stories',
  children: [{
    value: 'Set security questions',
    id: 'set-security-questions',
    children: []
  }, {
    value: 'Login with username &amp; password',
    id: 'login-with-username--password',
    children: []
  }, {
    value: 'Unlock application with PIN',
    id: 'unlock-application-with-pin',
    children: []
  }, {
    value: 'Forgot username or password',
    id: 'forgot-username-or-password',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`This functionality supports users logging in for the first time as well as the ongoing secure identification and provision of access to users.`}</p>
    <h2 {...{
      "id": "configuration"
    }}>{`Configuration`}</h2>
    <p>{`The frequency of when the user is required to login with username and password can be configured.`}</p>
    <p><em parentName="p">{`Recommended:`}</em></p>
    <ul>
      <li parentName="ul">{`In countries where connectivity is good, you can set this more frequently e.g. the user must login with username and password every week.`}</li>
      <li parentName="ul">{`In countries where connectivity is less good, you can set this less frequently so that users can unlock the application primarily using the PIN.`}</li>
    </ul>
    <p>{`NB. Security considerations should be taken into account when making this decision.`}</p>
    <h2 {...{
      "id": "user-stories"
    }}>{`User Stories`}</h2>
    <p>{`As `}<strong parentName="p">{`a user`}</strong>{`, I want to be able to be able to login to the application so that I can conduct my work.`}</p>
    <p>{`As `}<strong parentName="p">{`a user`}</strong>{`, I want to be able to be able to reset my password in case I forget it, so that I can log back in and continue working.`}</p>
    <p>{`As `}<strong parentName="p">{`a user`}</strong>{`, I want to be able to reset my username in case I forget it, so that I can log back in and continue working.`}</p>
    <p>{`As `}<strong parentName="p">{`a use`}</strong>{`r I want to be able to easily access the application without using my username and password every time, so that I can save time.`}</p>
    <h1 {...{
      "id": "functionality"
    }}>{`Functionality`}</h1>
    <h3 {...{
      "id": "set-security-questions"
    }}>{`Set security questions`}</h3>
    <p>{`The first the time user logs in they use a username and password that is generated for them based on their first and last name. On first login, the user is required to:`}</p>
    <ol>
      <li parentName="ol">{`Choose a new secure password`}</li>
      <li parentName="ol">{`Set 3 security questions that can be used to verify identity at a later point`}</li>
      <li parentName="ol">{`Confirm their details and update as required`}</li>
    </ol>
    <img alt="Security_questions" src={useBaseUrl("assets/support_functions/Login_8db5ac07d5ed48599beebedfb58dd273/Security_questions.png")} />
    <img alt="Set_password" src={useBaseUrl("assets/support_functions/Login_8db5ac07d5ed48599beebedfb58dd273/Set_password.png")} />
    <h3 {...{
      "id": "login-with-username--password"
    }}>{`Login with username & password`}</h3>
    <p><strong parentName="p">{`First login`}</strong></p>
    <p>{`Once the username and password is set, the user will login for the first time. This login process is the same from this point forward and will be required at a frequency as per the configuration made. In Zambia, this is set to request login details once per week.`}</p>
    <p><strong parentName="p">{`Two-factor Authentication:`}</strong>{` When the user logs in with their username and password, they will be sent a 6 digit verification code to the mobile phone number associated with their account. Upon entering this code, the user will be taken to the homepage of their application, dependent on their `}<a parentName="p" {...{
        "href": "../system_overview/user_types/overviewUserTypes"
      }}>{`User types`}</a>{`.`}</p>
    <h3 {...{
      "id": "unlock-application-with-pin"
    }}>{`Unlock application with PIN`}</h3>
    <p><strong parentName="p">{`First login`}</strong></p>
    <p>{`The first time the user logs into the application with their username and password, they will be prompted to enter a 4 digit PIN. This PIN will allow them to quickly access the application when the application locks itself, preventing access by anyone other than the user and meaning the user does not need to enter their username and password and authenticate every time they use the app.`}</p>
    <img alt="FA_Tablet_SetPin-2" src={useBaseUrl("assets/support_functions/Login_8db5ac07d5ed48599beebedfb58dd273/FA_Tablet_SetPin-2.png")} />
    <img alt="Tablet_7inch" src={useBaseUrl("assets/support_functions/Login_8db5ac07d5ed48599beebedfb58dd273/Tablet_7inch.1.1.1.1.1.1.1.1.png")} />
    <h3 {...{
      "id": "forgot-username-or-password"
    }}>{`Forgot username or password`}</h3>
    <p>{`If the user forgets their username or password, they can create a new one by following the "Can't Login" steps from the login screen.`}</p>
    <p>{`The user is required to:`}</p>
    <ol>
      <li parentName="ol">{`Provide the phone number associated with the account`}</li>
      <li parentName="ol">{`Enter a verification code sent to them on SMS`}</li>
      <li parentName="ol">{`Answer one of the 3 security questions we asked them to create during the onboarding process`}</li>
    </ol>
    <p>{`Upon successful completion of these steps, the user can create a new password or is sent a reminder of their username.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
