  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'userTeam',
	title: 'User & team management'
};
export const metadata = {
  "id": "system_admin/userTeam",
  "title": "User & team management",
  "description": "The functionality supports essential system administration activities, managing both users and teams in the system.",
  "source": "@site/docs/system_admin/User_team_management_344da97053e34b9e9b87bdd5c5e3e68f.md",
  "permalink": "/opencrvs-core/docs/system_admin/userTeam",
  "sidebar": "docs",
  "previous": {
    "title": "User support",
    "permalink": "/opencrvs-core/docs/support_functions/userSupport"
  },
  "next": {
    "title": "Content management",
    "permalink": "/opencrvs-core/docs/system_admin/contentManagement"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Configuration',
  id: 'configuration',
  children: []
}, {
  value: 'User Stories',
  id: 'user-stories',
  children: [{
    value: 'Create a new user',
    id: 'create-a-new-user',
    children: []
  }, {
    value: 'Edit user details',
    id: 'edit-user-details',
    children: []
  }, {
    value: 'Create &amp; View office',
    id: 'create--view-office',
    children: []
  }]
}, {
  value: '<em>Coming Soon</em>',
  id: 'coming-soon',
  children: [{
    value: 'Deactivate users',
    id: 'deactivate-users',
    children: []
  }, {
    value: 'Request the creation of new users',
    id: 'request-the-creation-of-new-users',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`The functionality supports essential system administration activities, managing both users and teams in the system.`}</p>
    <h2 {...{
      "id": "configuration"
    }}>{`Configuration`}</h2>
    <p>{`Standard user roles are defined in `}<a parentName="p" {...{
        "href": "https://www.notion.so/User-types-06ba450af0ea4de0adf3239dbda5f95f"
      }}>{`User types`}</a>{`. If you want to change these user types, this will require customisation.`}</p>
    <p>{`The details required for each user can be configured as per country requirements.`}</p>
    <h2 {...{
      "id": "user-stories"
    }}>{`User Stories`}</h2>
    <ul>
      <li parentName="ul">{`As a `}<strong parentName="li">{`System Administrator,`}</strong>{` I want to be able to add users to the system so that they can conduct their activities`}</li>
      <li parentName="ul">{`As a `}<strong parentName="li">{`System Administrator,`}</strong>{` I want to be able to edit user details so that they are always up to date and the user can access the application as and when needed`}</li>
      <li parentName="ul">{`As a `}<strong parentName="li">{`System Administrator,`}</strong>{` I want to be able to be able to view all users from the same office so that I can see who works there `}<strong parentName="li">{`[in roadmap]`}</strong></li>
      <li parentName="ul">{`As a `}<strong parentName="li">{`System Administrator,`}</strong>{` I want to be able to see which users are active and which users are no longer active so that I know who is working in a specific office`}</li>
      <li parentName="ul">{`As a `}<strong parentName="li">{`Registrar,`}</strong>{` I want to be able to be able to view all users in my office so that only active users have access to the application `}<strong parentName="li">{`[in roadmap]`}</strong></li>
      <li parentName="ul">{`As a `}<strong parentName="li">{`Registrar`}</strong>{`, I want to be able to request the creation of a new user so that they can quickly get to work `}<strong parentName="li">{`[in roadmap]`}</strong></li>
      <li parentName="ul">{`As a `}<strong parentName="li">{`Registrar,`}</strong>{` I want to be able to request to deactivate users who no longer work on civil registration so that they can't conduct activities when they don't have an official role `}<strong parentName="li">{`[in roadmap]`}</strong></li>
    </ul>
    <h1 {...{
      "id": "functionality"
    }}>{`Functionality`}</h1>
    <h3 {...{
      "id": "create-a-new-user"
    }}>{`Create a new user`}</h3>
    <p>{`The `}<strong parentName="p">{`System Administrator`}</strong>{` can add users and assign them to an office.`}</p>
    <p>{`They will need a valid mobile phone number and National ID (if the country has one) as standard.`}</p>
    <h3 {...{
      "id": "edit-user-details"
    }}>{`Edit user details`}</h3>
    <p>{`The `}<strong parentName="p">{`System Administrator`}</strong>{` can edit the user details as required.`}</p>
    <p>{`Any changes made to user details are tracked as part of the audit functionality.`}</p>
    <h3 {...{
      "id": "create--view-office"
    }}>{`Create & View office`}</h3>
    <p>{`The `}<strong parentName="p">{`System Administrator`}</strong>{` can create new offices when they are established`}</p>
    <p>{`The `}<strong parentName="p">{`System Administrator`}</strong>{` can search for offices and view all actors assigned to that office`}</p>
    <p>{`The `}<strong parentName="p">{`Registrar`}</strong>{` can view all actors assigned to their office`}</p>
    <h2 {...{
      "id": "coming-soon"
    }}><em parentName="h2">{`Coming Soon`}</em></h2>
    <h3 {...{
      "id": "deactivate-users"
    }}>{`Deactivate users`}</h3>
    <p>{`The `}<strong parentName="p">{`System Administrator`}</strong>{` can deactivate a user`}</p>
    <h3 {...{
      "id": "request-the-creation-of-new-users"
    }}>{`Request the creation of new users`}</h3>
    <p>{`The `}<strong parentName="p">{`Registrar`}</strong>{` can request the creation of a new user through the system so that all new employees can be added to the system efficiently`}</p>
    <p>{`The `}<strong parentName="p">{`Registrar`}</strong>{` can request the deactivation of a user through the system`}</p>
    <p>{`The `}<strong parentName="p">{`System Administrator/Registration Manager`}</strong>{` can review new user / deactivation requests and approve or reject them`}</p>
    <p>{`The `}<strong parentName="p">{`Registrar`}</strong>{` can see the status of their new user / deactivation requests`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
