export const rightToc = [{
  value: 'Workqueues',
  id: 'workqueues',
  children: []
}, {
  value: 'Example users',
  id: 'example-users',
  children: []
}, {
  value: 'Site Map',
  id: 'site-map',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "field-agents"
    }}>{`Field agents`}</h1>
    <p>{`Created: May 15, 2020 8:49 AM`}</p>
    <p>{`This user is usually a community actor who operates in some capacity at the community level e.g. Community Health Worker, local leader, police`}</p>
    <ul>
      <li parentName="ul">{`Able to create birth and death registration applications using a tablet/mobile device and send these for review by registration staff`}</li>
    </ul>
    <h3 {...{
      "id": "workqueues"
    }}>{`Workqueues`}</h3>
    <p>{`The Field Agent sees 3 work-queues to help them effectively manage their workload:`}</p>
    <ol>
      <li parentName="ol"><strong parentName="li">{`In progress:`}</strong>{` any draft applications that they have started and not completed. From this workqueue, the field agent can complete and submit the application. `}</li>
      <li parentName="ol"><strong parentName="li">{`Sent for review:`}</strong>{` any applications that they have submitted for review (complete with all mandatory information) or for follow-up (without all mandatory information).`}</li>
      <li parentName="ol"><strong parentName="li">{`Require updates:`}</strong>{` any applications that they have submitted that have been rejected by the Registrar because something is wrong with the application. The field agent can see what updates are required and can advise the applicant to go to the registration office to make necessary updates.  `}</li>
    </ol>
    <p><img parentName="p" {...{
        "src": "Field%20agents%20521a5d4cb8a44a61b98ae30ed5a74628/Field_Agent_Home.png",
        "alt": "Field%20agents%20521a5d4cb8a44a61b98ae30ed5a74628/Field_Agent_Home.png"
      }}></img></p>
    <p><img parentName="p" {...{
        "src": "Field%20agents%20521a5d4cb8a44a61b98ae30ed5a74628/register.opencrvs.qa1.jembi.org__tokeneyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9.eyJzY29wZSI6WyJkZWNsYXJlIiwiZGVtbyJdLCJpYXQiOjE1ODkzNzA5OTMsImV4cCI6MTU4OTk3NTc5MywiYXVkIjpbIm9wZW5jcnZzOmF1dGgtdXNlciIsIm9wZW5jcnZzOnVzZXItbWdudC11c2VyIiwib3BlbmNy.png",
        "alt": "Field%20agents%20521a5d4cb8a44a61b98ae30ed5a74628/register.opencrvs.qa1.jembi.org__tokeneyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9.eyJzY29wZSI6WyJkZWNsYXJlIiwiZGVtbyJdLCJpYXQiOjE1ODkzNzA5OTMsImV4cCI6MTU4OTk3NTc5MywiYXVkIjpbIm9wZW5jcnZzOmF1dGgtdXNlciIsIm9wZW5jcnZzOnVzZXItbWdudC11c2VyIiwib3BlbmNy.png"
      }}></img></p>
    <h3 {...{
      "id": "example-users"
    }}>{`Example users`}</h3>
    <p><strong parentName="p">{`Health Assistant in Bangladesh`}</strong></p>
    <p>{`Sammi is a Community Health Assistant in Bangladesh. She is responsible for providing maternal and child health care and vaccination services in her community. She is comfortable  using the tablet because she already had a smartphone and loves using OpenCRVS in the community: "By offering this service at their homes, this will make so many people's lives easier."`}</p>
    <p><img parentName="p" {...{
        "src": "Field%20agents%20521a5d4cb8a44a61b98ae30ed5a74628/Sammi_Portrait.jpg",
        "alt": "Field%20agents%20521a5d4cb8a44a61b98ae30ed5a74628/Sammi_Portrait.jpg"
      }}></img></p>
    <p><strong parentName="p">{`Nurse in Zambia`}</strong></p>
    <p>{`Maneya Mwansakilwa is a Nurse in Kanyama hospital in Lusaka, Zambia. She provides maternal and child health services in the hospital and in the community. She tells us that a large number of births occur in the community and these mothers often do not visit the hospital for services for their babies, they wait for community visits, "...and so if this process can be brought nearer to the people, it will do a lot of good to them".`}</p>
    <p><img parentName="p" {...{
        "src": "Field%20agents%20521a5d4cb8a44a61b98ae30ed5a74628/Zambia_CHW.jpg",
        "alt": "Field%20agents%20521a5d4cb8a44a61b98ae30ed5a74628/Zambia_CHW.jpg"
      }}></img></p>
    <h3 {...{
      "id": "site-map"
    }}>{`Site Map`}</h3>
    <p>{`The functions available to the Field Agent are visible to the user as per the below sitemap.`}</p>
    <p><img parentName="p" {...{
        "src": "Field%20agents%20521a5d4cb8a44a61b98ae30ed5a74628/ia_fieldAgent2x.png",
        "alt": "Field%20agents%20521a5d4cb8a44a61b98ae30ed5a74628/ia_fieldAgent2x.png"
      }}></img></p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
