  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'notifyAVitalEvent',
	title: 'Notify a vital event'
};
export const metadata = {
  "id": "core_functions/notifyAVitalEvent",
  "title": "Notify a vital event",
  "description": "This functionality allows a Registration Agent or a Registrar to view birth and death notifications sent from external health facilities. These notifications appear in in the \"In-Progress\" workqueue as they require follow up work to complete all mandatory fields.",
  "source": "@site/../docs/core_functions/Notify_a_vital_event_44dd01fc728c4d7f8b88171d079d71d2.mdx",
