  "sidebar": "docs",
  "previous": {
    "title": "Introduction",
    "permalink": "/opencrvs-core/docs/introduction"
  }
};
  /* @jsx mdx */
import useBaseUrl from "@docusaurus/useBaseUrl";
export const rightToc = [{
  value: 'Configuration',
  id: 'configuration',
  children: []
}, {
  value: 'User Stories',
  id: 'user-stories',
  children: [{
    value: '<strong>View notifications from external system e.g. health (DHIS2)</strong>',
    id: 'view-notifications-from-external-system-eg-health-dhis2',
    children: []
  }, {
    value: 'View incomplete applications sent from the field',
    id: 'view-incomplete-applications-sent-from-the-field',
    children: []
  }, {
    value: 'Complete notification',
    id: 'complete-notification',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`This functionality allows a `}<a parentName="p" {...{
        "href": "https://www.notion.so/Registration-Agent-7fe5e31c8fae4d3981e0d4f69576c084"
      }}><strong parentName="a">{`Registration Agent`}</strong></a>{` or a `}<a parentName="p" {...{
        "href": "https://www.notion.so/Registrar-3a66b6ccf39b49708baebeb802e1bc1f"
      }}><strong parentName="a">{`Registrar`}</strong></a>{` to view birth and death notifications sent from external health facilities. These notifications appear in in the "In-Progress" workqueue as they require follow up work to complete all mandatory fields.`}</p>
    <h2 {...{
      "id": "configuration"
    }}>{`Configuration`}</h2>
    <p>{`Notifications can be received from any external system and an additional workqueue can be added so that users know where the notification came from.`}</p>
    <p>{`In Bangladesh, OpenCRVS receives notifications from DHIS2.`}</p>
    <h2 {...{
      "id": "user-stories"
    }}>{`User Stories`}</h2>
    <p>{`As a `}<strong parentName="p">{`Health Worker,`}</strong>{` I want to be able to send notifications of birth and death from the hospital, so that they trigger the civil registration process.`}</p>
    <p>{`As a `}<strong parentName="p">{`Registration Agent`}</strong>{`, I want to be able to see notifications that come from external health systems and update them, so that I can send them for approval to the Registrar.`}</p>
    <p>{`As a `}<strong parentName="p">{`Registrar`}</strong>{`, I want to be able to see notifications that come from external health systems and update them, so that I can register them.`}</p>
    <p>{`As a `}<strong parentName="p">{`Registration Agent`}</strong>{`, I want to be able to see incomplete applications that have been sent from the field and update them, so that I can send them for approval to the Registrar.`}</p>
    <p>{`As a `}<strong parentName="p">{`Registrar`}</strong>{`, I want to be able to see incomplete applications that have been sent from the field and update them, so that I can register them.`}</p>
    <h1 {...{
      "id": "functionality"
    }}>{`Functionality`}</h1>
    <h3 {...{
      "id": "view-notifications-from-external-system-eg-health-dhis2"
    }}><strong parentName="h3">{`View notifications from external system e.g. health (DHIS2)`}</strong></h3>
    <p>{`Registration Agents and Registrars can see notifications sent from an external health system in the `}<em parentName="p">{`In-progress`}</em>{` workqueue. They can also see which health facility they were sent from and when.`}</p>
    <p><img parentName="p" {...{
        "src": "assets/core_functions/Notify_a_vital_event_44dd01fc728c4d7f8b88171d079d71d2/Health_Notification.png",
        "alt": "assets/core_functions/Notify_a_vital_event_44dd01fc728c4d7f8b88171d079d71d2/Health_Notification.png"
      }}></img></p>
    <h3 {...{
      "id": "view-incomplete-applications-sent-from-the-field"
    }}>{`View incomplete applications sent from the field`}</h3>
    <p>{`Registration Agents and Registrars can see notifications sent from field agents that do not have all mandatory information included in the `}<em parentName="p">{`In-progress`}</em>{` workqueue. They can also see which agent sent the incomplete application, when and from where.`}</p>
    <h3 {...{
      "id": "complete-notification"
    }}>{`Complete notification`}</h3>
    <p>{`From this workqueue the user can download the notification, review the information provided, and update it with the information required to submit it as a complete application or register it. To encourage active follow-up, they can see the contact number of the Applicant in the workqueue, it is then their responsibility to follow-up with the Applicant to complete the mandatory information and get supporting documents.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
