  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'overviewUserTypes',
	title: 'Overview'
};
export const metadata = {
  "id": "system_overview/user_types/overviewUserTypes",
  "title": "Overview",
  "description": "OpenCRVS has 6 user types that reflect common user roles present in civil registration workflows. Each user has different roles and responsibilities and has a different set of workqueues that helps them manage their work.",
