  "permalink": "/opencrvs-core/docs/system_overview/user_types/overviewUserTypes",
  "sidebar": "docs",
  "previous": {
    "title": "Site Map",
    "permalink": "/opencrvs-core/docs/system_overview/siteMap"
  },
  "next": {
    "title": "Field agents",
    "permalink": "/opencrvs-core/docs/system_overview/user_types/fieldAgents"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Field agent',
  id: 'field-agent',
  children: []
}, {
  value: 'Registration agent',
  id: 'registration-agent',
  children: []
}, {
  value: 'Registration agent with delegated authority',
  id: 'registration-agent-with-delegated-authority',
  children: []
}, {
  value: 'Registrar',
  id: 'registrar',
  children: []
}, {
  value: 'Registration manager',
  id: 'registration-manager',
  children: []
}, {
  value: 'System administrator',
  id: 'system-administrator',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`OpenCRVS has 6 user types that reflect common user roles present in civil registration workflows. Each user has different roles and responsibilities and has a different set of workqueues that helps them manage their work.`}</p>
    <h3 {...{
      "id": "field-agent"
    }}>{`Field agent`}</h3>
    <p>{`This user is usually a community actor who operates in some capacity at the community level e.g. Community Health Worker, local leader, police`}</p>
    <ul>
      <li parentName="ul">{`Able to create birth and death registration applications using a tablet/mobile device and send these for review by registration staff`}</li>
      <li parentName="ul">{`Able to view their performance stats`}</li>
    </ul>
    <h3 {...{
      "id": "registration-agent"
    }}>{`Registration agent`}</h3>
    <p>{`This user responds to the reality in many countries that the Registrar does not conduct digital activities and instead delegates this responsibility to a data clerk of some description.`}</p>
    <ul>
      <li parentName="ul">{`Able to create birth and death registration applications in an office setting and send these for approval by the Registrar`}</li>
      <li parentName="ul">{`Able to review and validate applications sent from a field agent`}</li>
      <li parentName="ul">{`Able to send reviewed applications for approval and registration by the Registrar`}</li>
      <li parentName="ul">{`Able to request a correction to a record to the Registrar`}</li>
    </ul>
    <h3 {...{
      "id": "registration-agent-with-delegated-authority"
    }}>{`Registration agent with delegated authority`}</h3>
    <p>{`This user type extends the Registration Agents permissions, as detailed above, to allow the Registration agent to (i) register vital events and (ii) issue a certificate with the digital signature of the Registrar, with the authority delegated by the Registrar themselves.`}</p>
    <ul>
      <li parentName="ul">{`Able to create birth and death registration applications and register these events`}</li>
      <li parentName="ul">{`Able to request a correction to a record to Registration Manager`}</li>
      <li parentName="ul">{`Able to assign a Registrar's digital signature to a certificate and issue it`}</li>
    </ul>
    <h3 {...{
      "id": "registrar"
    }}>{`Registrar`}</h3>
    <p>{`This user is the officially mandated individual responsible for civil registration`}</p>
    <ul>
      <li parentName="ul">{`Able to create birth and death registration applications and register these events`}</li>
      <li parentName="ul">{`Able to view performance reports`}</li>
      <li parentName="ul">{`Able to view operational dashboard`}</li>
      <li parentName="ul">{`Able to review record correction requests and send for approval to Registration Manager`}</li>
      <li parentName="ul">{`Able to request a correction to a record to Registration Manager`}</li>
      <li parentName="ul">{`Able to audit users and records`}</li>
    </ul>
    <h3 {...{
      "id": "registration-manager"
    }}>{`Registration manager`}</h3>
    <p>{`This user is responsible for reviewing and processing record correction requests and viewing performance management reports and dashboards`}</p>
    <ul>
      <li parentName="ul">{`Able to view performance reports`}</li>
      <li parentName="ul">{`Able to view operational dashboard`}</li>
      <li parentName="ul">{`Able to review, approve or reject record correction requests submitted from registration offices`}</li>
      <li parentName="ul">{`Able to audit users and records`}</li>
    </ul>
    <h3 {...{
      "id": "system-administrator"
    }}>{`System administrator`}</h3>
    <p>{`This user is responsible for administering the system and monitoring performance`}</p>
    <ul>
      <li parentName="ul">{`Able to create users and edit user details`}</li>
      <li parentName="ul">{`Able to add offices and edit office details`}</li>
      <li parentName="ul">{`Able to view performance reports`}</li>
      <li parentName="ul">{`Able to view operational dashboard`}</li>
      <li parentName="ul">{`Able to audit users and records`}</li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
