const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`This functionality creates accountability within the OpenCRVS system. It tracks every interaction a user makes as well as every action done to a record. Each of these interactions can be viewed by a Registrar or a Registration Manager.`}</p>
    <h2 {...{
      "id": "configuration"
    }}>{`Configuration`}</h2>
    <p>{`Who can access what audit information is configurable based on country requirements.`}</p>
    <h2 {...{
      "id": "user-stories"
    }}>{`User Stories`}</h2>
    <p>{`As someone who needs to investigate a record:`}</p>
    <ul>
      <li parentName="ul">{`I want to be able to search for a record using the tracking ID, BRN, Name of Newborn/Deceased, Date of event, Location of Event`}</li>
      <li parentName="ul">{`I want to be able view the history of a record, along with who did what to the record, and when, so that I can understand a record's history and identify potential mistakes / wrongdoing.`}</li>
      <li parentName="ul">{`I want to be able to easily see the profile of the user who conducted each action, including their office and contact number so I can get in touch with them`}</li>
      <li parentName="ul">{`I want to be able to easily navigate to the user profile and disable their account if they have been conducting activities that are inappropriate`}</li>
    </ul>
    <h1 {...{
      "id": "functionality"
    }}>{`Functionality`}</h1>
    <h2 {...{
      "id": "coming-soon"
    }}><em parentName="h2">{`Coming Soon`}</em></h2>
    <h3 {...{
      "id": "audit-user"
    }}>{`Audit User`}</h3>
    <p>{`Any user can be audited. Depending on the configured permissions, all actions taken by a user can be viewed and investigated.`}</p>
    <p><em parentName="p">{`Recommended`}</em>{`:`}</p>
    <ul>
      <li parentName="ul">{`The `}<strong parentName="li">{`Registrar`}</strong>{` can audit the actions of the `}<strong parentName="li">{`Registration`}</strong>{` and `}<strong parentName="li">{`Field Agents`}</strong>{` associated with their office.`}</li>
      <li parentName="ul">{`the `}<strong parentName="li">{`Registration Manager`}</strong>{` can audit the actions of any agent. This should be done at frequent intervals to continuously monitor and audit performance as well as when any unusual activities are identified.`}</li>
    </ul>
    <h3 {...{
      "id": "audit-record"
    }}>{`Audit Record`}</h3>
    <p>{`Any record can be audited. Depending on the configured permissions, all records can be viewed and investigated.`}</p>
    <p><em parentName="p">{`Recommended`}</em>{`:`}</p>
    <ul>
      <li parentName="ul">{`The `}<strong parentName="li">{`Registrar`}</strong>{` can audit records associated with their office.`}</li>
      <li parentName="ul">{`the `}<strong parentName="li">{`Registration Manager`}</strong>{` can audit the any record.`}</li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
