  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'configuration',
	title: 'Configuration'
};
export const metadata = {
  "id": "configuration",
  "title": "Configuration",
  "description": "You are viewing functional documentation that reflects OpenCRVS configured for the Zambia and Bangladesh contexts. In order to use OpenCRVS in your country you will need to configure your own Resources Package that will make OpenCRVS work for you e.g. show the right form fields, include the right users, show the right reference data, and conduct the right client-side validations on things like phone numbers.",
  "source": "@site/../docs/Configuration3eb99d0aa2a64f6dbfa192bb8234c1f4.md",
  "permalink": "/opencrvs-core/docs/configuration",
  "sidebar": "docs",
  "previous": {
    "title": "Interoperability",
    "permalink": "/opencrvs-core/docs/interoperability"
  },
  "next": {
    "title": "Site Map",
    "permalink": "/opencrvs-core/docs/siteMap"
  }
};
  /* @jsx mdx */

export const rightToc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`You are viewing functional documentation that reflects OpenCRVS configured for the Zambia and Bangladesh contexts. In order to use OpenCRVS in your country you will need to configure your own `}<em parentName="p">{`Resources Package`}</em>{` that will make OpenCRVS work for you e.g. show the right form fields, include the right users, show the right reference data, and conduct the right client-side validations on things like phone numbers.`}</p>
    <h1 {...{
      "id": "what-you-need-to-configure-opencrvs-in-your-country"
    }}>{`What you need to configure OpenCRVS in your country`}</h1>
    <p>{`To configure your resources package, follow the instructions detailed in GitHub.`}</p>
    <p><a parentName="p" {...{
        "href": "https://github.com/opencrvs/opencrvs-zambia#can-you-explain-the-resources-module-features-in-more-detail"
      }}>{`opencrvs/opencrvs-zambia`}</a></p>
    <p>{`The resources package requires the following items:`}</p>
    <ul>
      <li parentName="ul">{`Default, factory settings database backup zips containing development users, facilities and administrative locations for the nation saved as FHIR resources.`}</li>
      <li parentName="ul">{`Birth and Death registration form configuration as JSON`}</li>
      <li parentName="ul">{`Birth and Death certificate PDF templates as JSON`}</li>
      <li parentName="ul">{`Internationalised text as JSON`}</li>
      <li parentName="ul">{`SMS gateway provider selection between `}<a parentName="li" {...{
          "href": "https://www.clickatell.com/"
        }}>{`Clickatell`}</a>{` & `}<a parentName="li" {...{
          "href": "https://www.infobip.com/"
        }}>{`Infobip`}</a></li>
      <li parentName="ul">{`Country specific environment variables in Docker compose files`}</li>
      <li parentName="ul">{`Country specific client config variables in .js files`}</li>
      <li parentName="ul">{`Logo for the Civil Registrar department that appears on the certificates and on the review page of the UI`}</li>
      <li parentName="ul">{`Any 3rd party API integrations you require into your OpenCRVS core`}</li>
    </ul>
    <h1 {...{
      "id": "how-to-get-ready-for-configuration"
    }}>{`How to get ready for configuration`}</h1>
    <p>{`Before you configure OpenCRVS for your context, we suggest spending some time to understand your country's needs and shape your requirements for your version of OpenCRVS around that. A good way of doing this is:`}</p>
    <ol>
      <li parentName="ol">{`Use the `}<strong parentName="li">{`CRVS Digitisation Guidebook`}</strong>{` to analyse and design your CRVS digitisation system.`}</li>
    </ol>
    <p><a parentName="p" {...{
        "href": "http://www.crvs-dgb.org/en/"
      }}>{`http://www.crvs-dgb.org/en/`}</a></p>
    <p><strong parentName="p">{`2. Conduct a Proof of Concept`}</strong></p>
    <p>{`A proof of concept is a demonstration, the purpose of which is to verify that certain concepts or theories have the potential for real-world application. The OpenCRVS PoC is therefore a version of OpenCRVS that is designed for you to determine feasibility. It requires 3-4 months (depending on your requirements) and the result is a version of OpenCRVS ready for testing in your country. The country configuration will be based on:`}</p>
    <ul>
      <li parentName="ul"><strong parentName="li">{`Design research:`}</strong>{` speak to civil registration service providers and customers and figure out what will work for them. Civil registration needs to be easy.`}</li>
      <li parentName="ul"><strong parentName="li">{`Requirements:`}</strong>{` these are outlined in the resources package above.`}</li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
