  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'registrar',
	title: 'Registrar'
};
export const metadata = {
  "id": "system_overview/user_types/registrar",
  "title": "Registrar",
  "description": "This user is the officially mandated individual responsible for civil registration",
  "source": "@site/docs/system_overview/user_types/Registrar_1ce886c049e04c01a4781fb4b97f8573.mdx",
  "permalink": "/opencrvs-core/docs/system_overview/user_types/registrar",
  "sidebar": "docs",
  "previous": {
    "title": "Registration Agent with delegated authority",
    "permalink": "/opencrvs-core/docs/system_overview/user_types/registrationAgentDelAuth"
  },
  "next": {
    "title": "Registration Manager",
    "permalink": "/opencrvs-core/docs/system_overview/user_types/registrationManager"
  }
};
  /* @jsx mdx */
import useBaseUrl from "@docusaurus/useBaseUrl";
export const rightToc = [{
  value: 'Workqueues',
  id: 'workqueues',
  children: []
}, {
  value: '<strong>Example Users</strong>',
  id: 'example-users',
  children: []
}, {
  value: 'Site Map',
  id: 'site-map',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`This user is the officially mandated individual responsible for civil registration`}</p>
    <ul>
      <li parentName="ul">{`Able to update incomplete applications from field agents`}</li>
      <li parentName="ul">{`Able to complete applications sent from DHIS2`}</li>
      <li parentName="ul">{`Able to create birth and death registration applications and register these events`}</li>
      <li parentName="ul">{`Able to view performance reports`}</li>
      <li parentName="ul">{`Able to view operational dashboard`}</li>
      <li parentName="ul">{`Able to review record correction requests and send for approval to Registration Manager`}</li>
      <li parentName="ul">{`Able to request a correction to a record to Registration Manager`}</li>
      <li parentName="ul">{`Able to audit users and records`}</li>
    </ul>
    <h3 {...{
      "id": "workqueues"
    }}>{`Workqueues`}</h3>
    <p>{`The Registrar sees 5 work-queues to help them effectively manage their workload:`}</p>
    <ol>
      <li parentName="ol"><strong parentName="li">{`In Progress:`}</strong></li>
    </ol>
    <ul>
      <li parentName="ul">{`Any draft applications that they have started and not completed.`}</li>
      <li parentName="ul">{`Any in-progress applications (without all mandatory fields completed) that they have received from field agents.`}</li>
      <li parentName="ul">{`Any notifications that they have received from health facilities and existing healthy systems e.g. DHIS2.`}</li>
    </ul>
    <p>{`From this workqueue, the registration agent can complete and submit the application.`}</p>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol"><strong parentName="li">{`Ready for review:`}</strong>{` any applications that have been submitted for review (complete with all mandatory information) from field agents.`}</li>
    </ol>
    <p>{`For offices where a `}<strong parentName="p">{`Registration Agent`}</strong>{` reviews applications before the Registrar, the Registrar can see this review has been completed when the record is marked with the below green tick.`}</p>
    <img alt="Reviewed_by_Registration_Agent" src={useBaseUrl("assets/system_overview/user_types/Registrar_1ce886c049e04c01a4781fb4b97f8573/Reviewed_by_Registration_Agent.png")} />
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">
        <p parentName="li"><strong parentName="p">{`Sent for updates:`}</strong>{` any applications that require updates before registration i.e. rejected with a reason - this requires follow-up over the phone or in-person.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li"><strong parentName="p">{`Ready to print:`}</strong>{` any applications that have been registered by the Registrar and the certificate is ready to print and be issued.`}</p>
      </li>
    </ol>
    <img alt="Registrar_Home" src={useBaseUrl("assets/system_overview/user_types/Registrar_1ce886c049e04c01a4781fb4b97f8573/Registrar_Home.png")} />
    <ol {...{
      "start": 5
    }}>
      <li parentName="ol"><strong parentName="li">{`Corrections requested:`}</strong>{` any corrections to a record requested by the Registration Agent that need to be reviewed and sent for approval to the Registration Manager.`}</li>
    </ol>
    <h3 {...{
      "id": "example-users"
    }}><strong parentName="h3">{`Example Users`}</strong></h3>
    <p><strong parentName="p">{`Union Parishad Secretary - Registrar, Bangladesh`}</strong></p>
    <p>{`Likhon is a UP Secretary in Joymonirhat Union Parishad, Bhurungamari, Bangladesh. He is passionate about all of the public services that he is responsible for and is always looking for better ways of doing things. He is happy using OpenCRVS because "the service is taken to those that live far from this office. Using the system I can see all the details and documents and register the birth or death here, before they travel here, only once!".`}</p>
    <img alt="Untitled" src={useBaseUrl("assets/system_overview/user_types/Registrar_1ce886c049e04c01a4781fb4b97f8573/Untitled.png")} />
    <h3 {...{
      "id": "site-map"
    }}>{`Site Map`}</h3>
    <p>{`The functions available to the Registrar are visible to the user as per the below sitemap.`}</p>
    <img alt="ia_registrar2x" src={useBaseUrl("assets/system_overview/user_types/Registrar_1ce886c049e04c01a4781fb4b97f8573/ia_registrar2x.png")} />
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
