  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'registrationAgent',
	title: 'Registration Agent'
};
export const metadata = {
  "id": "system_overview/user_types/registrationAgent",
  "title": "Registration Agent",
  "description": "This user responds to the reality in many countries that the Registrar does not conduct digital activities and instead delegates this responsibility to a data clerk of some description.",
  "source": "@site/docs/system_overview/user_types/Registration_Agent_95933beff2734bc385e9ce7cd0b6dfe8.mdx",
  "permalink": "/opencrvs-core/docs/system_overview/user_types/registrationAgent",
  "sidebar": "docs",
  "previous": {
    "title": "Field agents",
    "permalink": "/opencrvs-core/docs/system_overview/user_types/fieldAgents"
  },
  "next": {
    "title": "Registration Agent with delegated authority",
    "permalink": "/opencrvs-core/docs/system_overview/user_types/registrationAgentDelAuth"
  }
};
  /* @jsx mdx */
import useBaseUrl from "@docusaurus/useBaseUrl";
export const rightToc = [{
  value: 'Workqueues',
  id: 'workqueues',
  children: []
}, {
  value: 'Example users',
  id: 'example-users',
  children: []
}, {
  value: 'Site map',
  id: 'site-map',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <p>{`This user responds to the reality in many countries that the Registrar does not conduct digital activities and instead delegates this responsibility to a data clerk of some description.`}</p>
    <ul>
      <li parentName="ul">{`Able to update incomplete applications from field agents`}</li>
      <li parentName="ul">{`Able to complete applications sent from DHIS2`}</li>
      <li parentName="ul">{`Able to create birth and death registration applications in an office setting and send these for review by the Registrar`}</li>
      <li parentName="ul">{`Able to review and validate applications sent from a field agent`}</li>
      <li parentName="ul">{`Able to send reviewed applications for review and registration by the Registrar`}</li>
      <li parentName="ul">{`Able to request a correction to a record to the Registrar`}</li>
    </ul>
    <h3 {...{
      "id": "workqueues"
    }}>{`Workqueues`}</h3>
    <p>{`The Registration Agent sees 5 work-queues to help them effectively manage their workload:`}</p>
    <ol>
      <li parentName="ol"><strong parentName="li">{`In Progress:`}</strong></li>
    </ol>
    <ul>
      <li parentName="ul">{`Any draft applications that they have started and not completed.`}</li>
      <li parentName="ul">{`Any incomplete applications (without all mandatory fields) received from field agents.`}</li>
      <li parentName="ul">{`Any notifications received from existing systems e.g. digital health systems like DHIS2`}</li>
    </ul>
    <p>{`From this workqueue, the registration agent can complete and submit the application.`}</p>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">
        <p parentName="li"><strong parentName="p">{`Ready for review:`}</strong>{` any applications that have been submitted for review (complete with all mandatory information) from field agents.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li"><strong parentName="p">{`Sent for updates:`}</strong>{` any applications that require updates before registration i.e. rejected with a reason - this requires follow-up over the phone or in-person.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li"><strong parentName="p">{`Sent for approval:`}</strong>{` any applications that have been reviewed, are correct as per their review, and require registration by the Registrar.`}</p>
      </li>
      <li parentName="ol">
        <p parentName="li"><strong parentName="p">{`Ready to print:`}</strong>{` any applications that have been registered by the Registrar and the certificate is ready to print and be issued.`}</p>
      </li>
    </ol>
    <img alt="Registration_Agent_Home" src={useBaseUrl("assets/system_overview/user_types/Registration_Agent_95933beff2734bc385e9ce7cd0b6dfe8/Registration_Agent_Home.png")} />
    <h3 {...{
      "id": "example-users"
    }}>{`Example users`}</h3>
    <p><strong parentName="p">{`Union Digital Centre (UDC) Entrepreneur in Bangladesh`}</strong></p>
    <p>{`Jamal is a UDC Entrepreneur in Joymonirhat Union Parishad, Bhurungamari, Bangladesh. He is responsible for digital birth and death registration activities at the registration office, supporting the Registrars in their official capacity. He loves how easy it is to review the applications because "I can see the application details and the supporting documents side-by-side. This makes it easy to check if the information is correct and I don't have to store paper documents in the office".`}</p>
    <img alt="UDC" src={useBaseUrl("assets/system_overview/user_types/Registration_Agent_95933beff2734bc385e9ce7cd0b6dfe8/UDC.png")} />
    <h3 {...{
      "id": "site-map"
    }}>{`Site map`}</h3>
    <p>{`The functions available to the Registration Agent are visible to the user as per the below sitemap.`}</p>
    <img alt="ia_regAgent2x" src={useBaseUrl("assets/system_overview/user_types/Registration_Agent_95933beff2734bc385e9ce7cd0b6dfe8/ia_regAgent2x.png")} />
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
