  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'contributing',
	title: 'Contributing'
};
export const metadata = {
  "id": "community/contributing",
  "title": "Contributing",
  "description": "Our development process",
  "source": "@site/docs/community/Contributing_7ce7383af70c4d76b72ef4080dcc0dab.md",
  "permalink": "/opencrvs-core/docs/community/contributing"
};
