export const rightToc = [{
  value: 'Our development process',
  id: 'our-development-process',
  children: [{
    value: 'Reporting new issues',
    id: 'reporting-new-issues',
    children: []
  }, {
    value: 'Reporting bugs',
    id: 'reporting-bugs',
    children: []
  }, {
    value: 'Reporting security bugs',
    id: 'reporting-security-bugs',
    children: []
  }, {
    value: 'Proposing a change',
    id: 'proposing-a-change',
    children: []
  }]
}, {
  value: 'Working on OpenCRVS code',
  id: 'working-on-opencrvs-code',
  children: [{
    value: 'Contributing code',
    id: 'contributing-code',
    children: []
  }, {
    value: 'Installation',
    id: 'installation',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "our-development-process"
    }}>{`Our development process`}</h2>
    <p>{`OpenCRVS uses `}<a parentName="p" {...{
        "href": "https://github.com/opencrvs/opencrvs-core"
      }}>{`GitHub`}</a>{` as its source of truth. The core team will be working directly there. All changes will be public from the beginning.`}</p>
    <h3 {...{
      "id": "reporting-new-issues"
    }}>{`Reporting new issues`}</h3>
    <p>{`We use `}<a parentName="p" {...{
        "href": "https://github.com/opencrvs/opencrvs-core/issues"
      }}>{`GitHub Issues`}</a>{` if you would like to raise a bug or propose a change or feature. If you just have a general topic of discussion, or would like some technical help, please chat with us on `}<a parentName="p" {...{
        "href": "https://gitter.im/opencrvs/community"
      }}>{`Gitter`}</a>{`.`}</p>
    <h3 {...{
      "id": "reporting-bugs"
    }}>{`Reporting bugs`}</h3>
    <p>{`We use `}<a parentName="p" {...{
        "href": "https://github.com/opencrvs/opencrvs-core/issues"
      }}>{`GitHub Issues`}</a>{` for our public bugs. If you would like to report a problem, take a look around and see if someone already opened an issue about it, or chat with us on `}<a parentName="p" {...{
        "href": "https://gitter.im/opencrvs/community"
      }}>{`Gitter`}</a>{`. If you are certain this is a new, unreported bug, you can submit a bug report.`}</p>
    <p>{`Take screenshots or record your screen. `}<a parentName="p" {...{
        "href": "https://www.loom.com/"
      }}>{`Loom`}</a>{` is a great tool you can use to record a video and paste a link to it into your bug. Open a bug report on `}<a parentName="p" {...{
        "href": "https://github.com/opencrvs/opencrvs-core/issues/new?assignees=&labels=%F0%9F%91%B9Bug&template=---bug.md&title="
      }}>{`Github`}</a>{` following the guidance.`}</p>
    <h3 {...{
      "id": "reporting-security-bugs"
    }}>{`Reporting security bugs`}</h3>
    <p>{`Make sure to include a detailed description of the bug. Open a bug report on `}<a parentName="p" {...{
        "href": "https://github.com/opencrvs/opencrvs-core/issues/new?assignees=&labels=%F0%9F%91%B9Bug&template=---bug.md&title="
      }}>{`Github`}</a>{` following the guidance.`}</p>
    <h3 {...{
      "id": "proposing-a-change"
    }}>{`Proposing a change`}</h3>
    <p>{`If you would like to request a new feature or enhancement but are not yet thinking about opening a pull request, you can also file an issue with `}<a parentName="p" {...{
        "href": "https://github.com/facebook/docusaurus/issues/new?template=feature.md"
      }}>{`t`}</a>{`he `}<a parentName="p" {...{
        "href": "https://github.com/opencrvs/opencrvs-core/issues/new?assignees=&labels=%E2%98%95%EF%B8%8F+Discussion&template=---feature.md&title="
      }}>{`feature template.`}</a></p>
    <h2 {...{
      "id": "working-on-opencrvs-code"
    }}>{`Working on OpenCRVS code`}</h2>
    <h3 {...{
      "id": "contributing-code"
    }}>{`Contributing code`}</h3>
    <p>{`Please review the `}<a parentName="p" {...{
        "href": "https://github.com/opencrvs/opencrvs-core/blob/master/CONTRIBUTING.md"
      }}>{`contributing`}</a>{` file, clone the repository and submit a pull request. The authors will review the code and merge it in if all is well. By contributing to the OpenCRVS code, you are conforming to the terms of the `}<a parentName="p" {...{
        "href": "https://www.opencrvs.org/license"
      }}>{`license`}</a>{`.`}</p>
    <h3 {...{
      "id": "installation"
    }}>{`Installation`}</h3>
    <p>{`Detailed installation instructions for setting up a development environment, or deploying OpenCRVS to a server cluster are outlined in the GitHub repo `}<a parentName="p" {...{
        "href": "https://github.com/opencrvs/opencrvs-core"
      }}>{`README`}</a>{`.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
